import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import { useState } from 'react';


import { Link, Routes, Route, useNavigate } from 'react-router-dom';

const Login = () => {

    const [password, setPassword] = useState("");
    const [email, setEmail] = useState("");
    const [passwordError, setpasswordError] = useState("");
    const [emailError, setemailError] = useState("");

    const handleValidation = (event) => {
        let formIsValid = true;

        if (!email.match(/^\w+@[a-zA-Z_]+?\.[a-zA-Z]{2,3}$/)) {
        formIsValid = false;
        setemailError("Email Not Valid");
        return false;
        } else {
        setemailError("");
        formIsValid = true;
        }

        if (!password.match(/^[a-zA-Z]{8,22}$/)) {
        formIsValid = false;
        setpasswordError(
            "Only Letters and length must best min 8 Chracters and Max 22 Chracters"
        );
        return false;
        } else {
        setpasswordError("");
        formIsValid = true;
        }

        return formIsValid;
    };

    const loginSubmit = (e) => {
        e.preventDefault();
        handleValidation();
    };

    return (
        <div className="login">
            <Container>
                <div className="row">
                    <div className="col-lg-6">
                        <h1 className="fw-bold">Welcome Back</h1>
                        <p className="">Hey, enter your details to continue</p>
                        <form id="loginform" onSubmit={loginSubmit}>
                            <div className="form-group">
                                <label>Your</label>
                                <input type="email" className="form-control" id="EmailInput" name="EmailInput" aria-describedby="emailHelp" placeholder="Enter email" onChange={(event) => setEmail(event.target.value)}/>
                            </div>
                        </form>
                    </div>
                    <div className="col-lg-6">
                        <h1>Image</h1>
                    </div>
                </div>
            </Container>
        </div>

      );
}

export default Login;
