import react, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/esm/Button';
import sport from '../img/sport-fct.png'

const PersonalLife = ({moodName, factor, setFactor, page, setPage}) => {

    const [selected, setSelected] = useState('sports');

    function handleChange(e){
        setSelected(e.target.value);
        setFactor(e.target.value);
    }

    function goNextPage(){
        setPage(page => page + 1);
    }

    function goBackPage(){
        setPage(page => page - 1)
    }


    return ( 
        <div className="p-life">
            <h5 className="fw-bold">Looks like you're feeling <b className="insertTxt">{moodName}</b></h5>
            <p>What focus area is contributing the most to this feeling?</p>
            <div className="options">
                <div className="form-check">
                    <img src={sport}></img>
                    <input value='sports' checked={selected === 'sports'} className="form-check-input" type="radio" name="inlineRadioOptions" id="inlineRadio1" onClick={handleChange}/>
                    <label className="form-check-label" for="inlineRadio1">My Sport Skills</label>
                </div>
                <div className="form-check">
                    <input value='personal' checked={selected === 'personal'} className="form-check-input" type="radio" name="inlineRadioOptions" id="inlineRadio2"  onClick={handleChange}/>
                    <label className="form-check-label" for="inlineRadio2">My Personal Life</label>
                </div>
                <div className="form-check">
                    <input value='academics' checked={selected === 'academics'} className="form-check-input" type="radio" name="inlineRadioOptions" id="inlineRadio3" onClick={handleChange}/>
                    <label className="form-check-label" for="inlineRadio3">My Academics</label>
                </div>
            </div>
            <button onClick={goBackPage} className='btn btn-secondary'>Back</button>
            <button onClick={goNextPage} className='btn btn-primary'>Next</button>

        </div>
     );
}

export default PersonalLife;
