import react, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/esm/Button';
import { Link, Routes, Route, useNavigate } from 'react-router-dom';

const DailyGoals = () => {

    const [isShown, setIsShown] = useState(false);
    const [isPShown, setIsPShown] = useState(false);
    const [isAShown, setIsAShown] = useState(false);

    const handleClickSports = (e) => {
        setIsShown(current => !current);
    };

    const handleClickLife = event => {
        setIsPShown(current => !current);

    };
    const handleClickSchool = event => {

    };

    return (
        <div className="daily-goals">
            <Navbar sticky="top" className="navbar mt-5">
                <Container display="fluid">
                    <Navbar.Brand href="#hero">Ulete</Navbar.Brand>
                    <Nav className="ml-auto">
                            <Nav.Link className="me-5 text-center fw-bold nav-item" href="#hero">Home</Nav.Link>
                            <Nav.Link className="me-5 text-cente fw-bold nav-item" href="#about">Development</Nav.Link>
                            <Nav.Link className="me-5 text-cente fw-bold nav-item" href="#about">Trackers</Nav.Link>
                            <Nav.Link className="me-5 text-cente fw-bold nav-item" href="#about">Resources</Nav.Link>
                            <i class="bi bi-person-circle "></i>
                    </Nav>
                </Container>
            </Navbar>
            <Container display="flex">
                <div className="daily-goals-txt mt-5">
                    <h2>Get Closer to Your Goals!</h2>
                    <p className="fw-bold">Here are some recommended tasks to help your performance</p>

                    <div className="options mt-5"> 
                        <button onClick={handleClickSports} id="sports" className="me-5 btn btn-opt fw-bold text-light">My Sport <i class="bi bi-activity"></i></button>
                        <button onClick={handleClickSports} className="me-5 btn btn-opt fw-bold text-light">My Personal Life <i class="bi bi-activity"></i></button>
                        <button onClick={handleClickSports} className="me-5 btn btn-opt fw-bold text-light">My Academics <i class="bi bi-activity"></i></button>
                    </div>
                    <div className="list">
                    {isShown && (
                            <div>
                            <h2>Some content here</h2>
                          </div>
                        )
                    }
                    {isPShown && (
                            <div>
                            <h2>Some perosn here</h2>
                          </div>
                        )
                    }
                    {isAShown && (
                            <div>
                            <h2>Some aca here</h2>
                          </div>
                        )
                    }
                    </div>
                </div>
            </Container>
        </div>
      );
}

export default DailyGoals;
