import react, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/esm/Button';
import { Link, Routes, Route, useNavigate } from 'react-router-dom';

const Goals = ({text, focus, setDailyGoals, dailyGoals}) => {
    // events

    const deleteHandler = () =>{
        setDailyGoals(dailyGoals.filter((el) => el.id !== dailyGoals.id))
    };

    return ( 
        <div className="goals">
            <Container display="flex">
                <li className="goals-item">{text} {focus}</li>
                <button className="btn check-mark"><i class="bi bi-check-square-fill"></i></button>
                <button className="btn trash" onClick={deleteHandler} ><i class="bi bi-x-square-fill"></i></button>
            </Container>
        </div>

     );
}

export default Goals;
