import react, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/esm/Button';
import { Link, Routes, Route, useNavigate } from 'react-router-dom';
import angry from '../img/angry.png';

const MoodSlider = () => {
    const moods = {angry, sad, neutral, happy, excited};
    const [mood, setMood] = useState(moods.neutral);

    const angry = angry;
    const sad = require('../img/sad.png');
    const neutral = require('../img/neutral.png');
    const happy = require('../img/happy.png');
    const excited = require('../img/excited.png');


    const handleChange = (e) =>{
        const val = e.target.value;
        if (val < 20) {
            setMood(moods.angry);
        } else if (val >= 20 && val < 40) {
            setMood(moods.sad);
        } else if (val >= 40 && val < 60) {
            setMood(moods.neutral);
        } else if (val >= 60 && val < 80) {
            setMood(moods.happy);
        } else if (val > 80) {
            setMood(moods.excited);
        }
    };

    return (  
        <div className="slider">
            <h4>How are you feeling today?</h4>

            <img src={mood} alt="mood"></img>

            <input type="range" list="stops" onChange={handleChange} min="1" max="100" />
            <datalist id="stops">
                <option value="really sad" label="😔"></option>
                <option value="sad" label="😔"></option>
                <option value="neutral" label="😐"></option>
                <option value="happy" label="😄"></option>
                <option value="really happy" label="😔"></option>
            </datalist>
        </div>

    );
}

export default MoodSlider;
