import react, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/esm/Button';
import { Link, Routes, Route, useNavigate } from 'react-router-dom';

const MoodSlider = ({setMood, mood, moods}) => {

    const navigate = useNavigate();

    const[page, setPage] = ([]);
    const[moodName, setMoodName] = useState("");

    const handleChange = (e) =>{
        const val = e.target.value;
        if (val < 20) {
            setMood(moods.angry);
            setMoodName("angry");
        } else if (val >= 20 && val < 40) {
            setMood(moods.sad);
            setMoodName("sad");

        } else if (val >= 40 && val < 60) {
            setMood(moods.neutral);
            setMoodName("neutral");

        } else if (val >= 60 && val < 80) {
            setMood(moods.happy);
            setMoodName("happy");

        } else if (val > 80) {
            setMood(moods.excited);
            setMoodName("excited");

        }
    };

    const nextHandler = (e) => {
        console.log(moods);
        // e.preventDefault();
        // setMood([
        //     ...mood, {mood: mood, page: page, id: Math.random() * 1000}])

    };



    return (  
        <div className="slider">
            <h4>How are you feeling today?</h4>
            <div className="moods">
                <img src={mood} alt="mood"></img>
            </div>
            <div className="track-class">
                <input type="range" list="stops" onChange={handleChange} min="1" max="100" />
            </div>
            <datalist id="stops">
                <option value="really sad" label="😔"></option>
                <option value="sad" label="😔"></option>
                <option value="neutral" label="😐"></option>
                <option value="happy" label="😄"></option>
                <option value="really happy" label="😔"></option>
            </datalist>
            <button className='btn btn-primary' onClick={nextHandler}>Next</button>
        </div>

    );
}

export default MoodSlider;
