import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import React, { useState } from 'react';
import ToggleButton from 'react-bootstrap/ToggleButton';
import ToggleButtonGroup from 'react-bootstrap/ToggleButtonGroup';
import { Link, Routes, Route, useNavigate } from 'react-router-dom';

const QuizTP = () => {
    const [value, setValue] = useState([1, 3]);

    const handleChange = (e) =>{
        const val = e.target.value; 
        if (val < 20) {
            setMood(moods.angry);
            setMoodName("angry");
        } else if (val >= 20 && val < 40) {
            setMood(moods.sad);
            setMoodName("sad");

        } else if (val >= 40 && val < 60) {
            setMood(moods.neutral);
            setMoodName("neutral");

        } else if (val >= 60 && val < 80) {
            setMood(moods.happy); 
            setMoodName("happy");

        } else if (val > 80) {
            setMood(moods.excited);
            setMoodName("excited");

        }
    };

    return ( 
        <div className="q-first-page">
        <Container display="flex">
            <div className="prog-bar">
                {/*The progress bar goes here*/}
            </div>

            <div className="form-title">
                <h4 className="fw-bold">How Often do you Practice per week?</h4>
                <p>It's all about load management</p>
            </div>

            <div className="slider-content">
                <div className="moods">
                    <img src={mood} alt="mood"></img>
                </div>
                <div className="track-class">
                    <input type="range" list="stops" onChange={handleChange} min="1" max="100" />
                </div>
                <datalist id="stops">
                    <option value="really sad" label="ðŸ˜”"></option>
                    <option value="sad" label="ðŸ˜”"></option>
                    <option value="neutral" label="ðŸ˜"></option>
                    <option value="happy" label="ðŸ˜„"></option>
                    <option value="really happy" label="ðŸ˜”"></option>
                </datalist>
            </div>





            {/*Back/Next btns*/}
            <div className="row align-items-center justify-content-between">
                <div className="col-lg-6">
                    <Link to="/questions" className="btn btn-secondary text-center fw-bold"> Back </Link>
                </div>
                <div className="col-lg-6">
                    <Link to="/confirm" className="btn btn-primary text-center fw-bold"> Next </Link>
                </div>
            </div>
        </Container>
        </div>

     );
}

export default QuizTP;
