import react, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/esm/Button';
import { Link, Routes, Route, useNavigate } from 'react-router-dom';
import {faPersonRunning} from '@fortawesome/free-solid-svg-icons'
import UserNavBar from './UserNavBar';
import MoodSlider from './MoodSlider';
import PersonalLife from './PersonalLife';
import Journal from './Journal';

const UserHome = () => {
    const angry = require('../img/angry.png');
    const sad = require('../img/sad.png');
    const neutral = require('../img/neutral.png');
    const happy = require('../img/happy.png');
    const excited = require('../img/excited.png');

    const [page, setPage] = useState(1);


    const moods = {angry, sad, neutral, happy, excited};
    const [mood, setMood] = useState(moods.neutral);


    function goNextPage(){
        setPage(page => page + 1)
    }

    function goBackPage(){
        setPage(page => page - 1)
    }

    return ( 
        <div className="user-home">
            <UserNavBar />
            <Container display="flex">
                <div className="welcome-txt mt-4">
                    <h1>Welcome back, Deborah</h1>
                    <p className="fw-bold">Letâ€™s get ready for an awesome day</p>
                </div>

                <div className="check-in p-5">
                    <div className="form">
                        <div className={page === 1 ? "active" : "completed" }>
                            <p>Comp 1</p>
                            <MoodSlider page={page} setPage={setPage} moods={moods} mood={mood} setMood={setMood}/>
                        </div>
                        <div className={page === 2 ? "active" : (page < 2 ? "inactive" : "completed")}>
                            <p>Component 2</p>
                        </div>
                        <div className={page === 3 ? "active" : (page < 3 ? "inactive" : "completed")}>
                            <p>Component 3</p>
                        </div>
                     </div>
                     <div className="component-div">
                        <div className='display'>
                            {page === 1 && <Component1 /> }
                            {page === 2 && <Component2 /> }
                            {page === 3 && <Component3 />}
                            {page === 4 && <Component4 />}
                        </div>

                        <button onClick={goBackPage} className='btns'>Back</button>
                        <button onClick={goNextPage}>Next</button>
                    </div> 
                </div>

                <div className="footer">

                </div>
            </Container>
        </div>
     );
}

export default UserHome;
