import react, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/esm/Button';
import { Link, Routes, Route, useNavigate } from 'react-router-dom';
import Angry from '../img/angry.png'

const MoodSlider = () => {
    const [mood, setMood] = useState([]);
    const angry = require('../img/angry.png');
    const sad = require('../img/sad.png');
    const neutral = require('../img/neutral.png');
    const happy = require('../img/happy.png');
    const excited = require('../img/excited.png');


    const handleChange = (e) =>{
        const val = e.target.value;
        if (val < 25) {
            setMood({});
        } else if (val >= 33 && val < 66) {
            setMood("😐");
        } else if (val > 66) {
            setMood("😎");
        }
    };

    return (  
        <div className="slider">
            <h4>How are you feeling today?</h4>
            <p style={{ fontSize: "150px" }}>{mood}</p>

            <input type="range" list="stops" onChange={handleChange} min="1" max="100" />
            <datalist id="stops">
                <option value="really sad" label="😔"></option>
                <option value="sad" label="😔"></option>
                <option value="neutral" label="😐"></option>
                <option value="happy" label="😄"></option>
                <option value="really happy" label="😔"></option>
            </datalist>
        </div>

    );
}

export default MoodSlider;
